import React from 'react';

export const validEmailRegex = RegExp(/^(([^<>()[\].,;:\s@"]+(\.[^<>()[\].,;:\s@"]+)*)|(".+"))@(([^<>()[\].,;:\s@"]+\.)+[^<>()[\].,;:\s@"]{2,})$/i);
export const onlyNumbers = RegExp(/^[0-9\b]+$/);

export const errors_messages = {
    id_number: 'הכנס 9 מספרים של ת.ז',
    tel_number:  'מספר טלפון מורכב מ10 תווים',
    password: 'סיסמה לפחות 4 תווים',
    year :'בחר שנת לימודים',
    email :  'אנא הכנס כתובת אימייל',
    gender : 'בחר מין',
}

export function validateForm(errors)  {
    let valid = true;
    Object.values(errors).forEach(val=>
         // if we have an error string set valid to false, will be true if val is not null
        val && (valid = false)
             
    );
    return valid;
}

export function check_and_assign_errors(name, value, temp_errors) {
    switch(name) {
        case 'email':
            if (!validEmailRegex.test(value)) {
                // is_form_valid = false;
                temp_errors.email = errors_messages.email;
                console.log(value);
            } else {
                temp_errors.email = null;
            }
            break;
        case 'id_number':
            if(value.length != 9 || !onlyNumbers.test(value)) {
                // is_form_valid = false;
                temp_errors.id_number = errors_messages.id_number;
                console.log(value);
            }else {
                temp_errors.id_number = null;
            }
            break;
        
        case 'year':
                if(!value) {
                    // is_form_valid = false;
                    temp_errors.year = errors_messages.year;
                    console.log(value);
                }else {
                    temp_errors.year = null;
                }
                break;
        case 'tel_numbeer':
                if(value.length != 10 || !onlyNumbers.test(value)) {
                    // is_form_valid = false;
                    temp_errors.tel_number = errors_messages.tel_number;
                    console.log(value);
                }else {
                    temp_errors.tel_number = null;
                }
                break;
        case 'gender':
                if(value != 'male' && value!='female') {
                    // is_form_valid = false;
                    temp_errors.gender = errors_messages.gender;
                    console.log(value);
                }else {
                    temp_errors.gender = null;
                }
                break;
        default:
        break;
    }
    return temp_errors
}
