import * as React from 'semantic-ui-react/dist/commonjs/modules/Accordion/node_modules/react'

import { default as AccordionAccordion, StrictAccordionAccordionProps } from 'semantic-ui-react/dist/commonjs/modules/Accordion/AccordionAccordion'
import AccordionContent from 'semantic-ui-react/dist/commonjs/modules/Accordion/AccordionContent'
import AccordionTitle from 'semantic-ui-react/dist/commonjs/modules/Accordion/AccordionTitle'

export interface AccordionProps extends StrictAccordionProps {
  [key: string]: any
}

export interface StrictAccordionProps extends StrictAccordionAccordionProps {
  /** Additional classes. */
  className?: string

  /** Format to take up the width of its container. */
  fluid?: boolean

  /** Format for dark backgrounds. */
  inverted?: boolean

  /** Adds some basic styling to accordion panels. */
  styled?: boolean
}

interface AccordionComponent extends React.ComponentClass<AccordionProps> {
  Accordion: typeof AccordionAccordion
  Content: typeof AccordionContent
  Title: typeof AccordionTitle
}

declare const Accordion: AccordionComponent

export default Accordion
