import React, { Component } from 'react'
// import {Form , Button} from 'react-bootstrap'
import {Form ,Input, Button, Checkbox, FormField} from 'semantic-ui-react'
import '../styles/login-form.scss'

class FormLogin extends Component  {
    constructor(props) {
        super(props)
        this.state = { 
            username: '', 
            password: '', 
            forgotPassword : false,
        }
    }

    handleChange = (e, { name, value }) => {
        this.setState({ [name]: value })
    }

    handleSubmit = () => {
        const { username, password } = this.state
        console.log(username, password)
    }
    handlePrint = () => {
        const { username, password } = this.state
        console.log(username, password)
    }
    WelcomeHeader = () => {
        return (
            <div>
                <h3 id="welcome">
                    ברוכים הבאים
                    
                </h3>
                <h4 id="welcome-to-where">
                    מערכת לניהול שעות החונכות
                </h4>
                <br></br>
            </div>
        )
    }

    render() {
        const { username, password } = this.state
        return (
            <div className="right-align">
                <this.WelcomeHeader/>
                <Form onSubmit={this.handleSubmit}>
                    <FormField>
                        <label>שם משתמש</label>
                        <Form.Input
                            placeholder='ת.ז'
                            name='username'
                            value={username}
                            onChange={this.handleChange}
                        />
                    </FormField>
                    
                    <FormField>
                        <label>סיסמה</label>
                        <Form.Input
                            type="password"
                            placeholder='סיסמה'
                            name='password'
                            value={password}
                            onChange={this.handleChange}
                        />
                    </FormField>
                    
                    

                    <Form.Field
                            control={Checkbox}
                            label='שכחתי סיסמה'
                        />

                    <Form.Field control={Button}>התחבר</Form.Field>
                    {/* <Button onClick={this.handlePrint}>print state</Button> */}
                </Form>
                
            </div>
        )
    }
}

export default FormLogin;
