import React, { Component } from 'react';
import { Container, Form, Input, Label } from 'semantic-ui-react';
import '../styles/signup-form.scss'
import '../styles/general.scss'


class FormSignup extends Component {
    constructor(props) {
        super(props)
        this.state = { 
            username: '', 
            password: '', 
            forgotPassword : false,
        }
        this.onSubmit = this.onSubmit.bind(this);
    }
    handleChange = (e, { name, value }) => {
        this.setState({ [name]: value })
    }
    onSubmit(e) {
        e.preventDefault();
        // const {username} = this.state
        // console.log(username);
        // axios.get('http://localhost:5000/users/'+ username)
        // .then(res => console.log("data: " + res.data.map(user => user.id_number)));
    }
    render() {
        const { username, password } = this.state

        return (
            <Form>
                <Form.Group widths='equal'>
                    <Form.Field>
                        <label>First name</label>
                        <Input fluid placeholder='First name' />
                    </Form.Field>
                    <Form.Field>
                        <label>מייל</label>
                        <Input fluid placeholder='Middle name' />
                    </Form.Field>
                    <Form.Field>
                        <label>שם פרטי</label>
                        <Input fluid placeholder='Last name' />
                    </Form.Field>
                </Form.Group>
                <Form.Group widths='equal'>
                    <Form.Field>
                        <label>סיסמה</label>
                        <Form.Input
                            
                            type="password"
                            placeholder='סיסמה'
                            name='password'
                            value={password}
                            onChange={this.handleChange}
                        />
                        {/* <Input fluid placeholder='First name' /> */}
                    </Form.Field>
                    <Form.Field>
                        <label>מס' טלפון</label>
                        <Input fluid placeholder='Middle name' />
                    </Form.Field>
                    <Form.Field>
                        <label>שם משפחה</label>
                        <Input fluid placeholder='Last name' />
                    </Form.Field>
                </Form.Group>
                <Form.Group widths='equal'>
                    <Form.Field>
                        <label>First name</label>
                        <Input fluid placeholder='First name' />
                    </Form.Field>
                    <Form.Field>
                        <label>Middle name</label>
                        <Input fluid placeholder='Middle name' />
                    </Form.Field>
                    <Form.Field>
                        <label>ת.ז</label>
                        <Input fluid placeholder='ת.ז' />
                        <Label size="tiny" pointing>שם המשתמש</Label>
                    </Form.Field>
                </Form.Group>
            </Form>
        )
    }
}

export default function SignupForm () {
    return (
        <Container className="signup-box">
            <div className="right-align">
                <FormSignup></FormSignup>   
            </div>
        </Container>
        
    )
}
