import React, { Component } from 'react';
import { Container, Form,  Label, Dropdown } from 'semantic-ui-react';
import '../styles/signup-form.scss'
import '../styles/general.scss'


class FormSignup extends Component {
    constructor(props) {
        super(props)
        this.state = { 
            first_name: '', 
            last_name: '', 
            id_number: '', 
            tel_number: '',
            password: '', 
            year : '',
            mail : '',
            adress : '',
        }
        this.onSubmit = this.onSubmit.bind(this);
    }
    handleChange = (e, { name, value }) => {
        this.setState({ [name]: value })
    }
    onSubmit(e) {
        e.preventDefault();
        const {first_name, last_name, id_number, tel_number, password, year, mail} = this.state
        console.log(first_name, last_name, id_number, tel_number, password, year, mail);
        
        
   
        // const {username} = this.state
        // console.log(username);
        // axios.get('http://localhost:5000/users/'+ username)
        // .then(res => console.log("data: " + res.data.map(user => user.id_number)));
    }
    

    render() {
        const {adress, first_name, last_name, id_number, tel_number, password, year, mail } = this.state
        const options = [
            { key: 'a', text: 'שנה א', value: 'year-a' },
            { key: 'b', text: 'שנה ב', value: 'year-b' },
            { key: 'c', text: 'שנה ג', value: 'year-c' },
            { key: 'd', text: 'שנה ד', value: 'year-d' },
            { key: 'e', text: 'שנה ה', value: 'year-e' },
        ]
        return (
            <Form>
                <Form.Group widths='equal'>
                    <Form.Field>
                        <label>שנת לימודים</label>
                        <Dropdown 
                        name='year'
                        value={year}
                        clearable 
                        options={options} 
                        selection 
                        onChange={this.handleChange} 
                        />
                        
                        {/* <Input fluid placeholder='First name' /> */}
                    </Form.Field>
                    <Form.Field>
                        <label>מייל</label>
                        <Form.Input
                            // type="password"
                            placeholder='@e-mail'
                            name='mail'
                            value={mail}
                            onChange={this.handleChange}
                        />
                    </Form.Field>
                    <Form.Field>
                        <label>שם פרטי</label>
                        <Form.Input
                            // type="password"
                            placeholder='שם פרטי'
                            name='first_name'
                            value={first_name}
                            onChange={this.handleChange}
                        />
                    </Form.Field>
                </Form.Group>
                <Form.Group widths='equal'>
                    <Form.Field>
                        <label>סיסמה</label>
                        <Form.Input
                            type="password"
                            placeholder='סיסמה'
                            name='password'
                            value={password}
                            onChange={this.handleChange}
                        />
                        {/* <Input fluid placeholder='First name' /> */}
                    </Form.Field>
                    <Form.Field>
                        <label>מס' טלפון</label>
                        <Form.Input
                            // type="password"
                            placeholder='טלפון'
                            name='tel_number'
                            value={tel_number}
                            onChange={this.handleChange}
                        />
                    </Form.Field>
                    <Form.Field>
                        <label>שם משפחה</label>
                        <Form.Input
                            // type="password"
                            placeholder='שם משפחה'
                            name='last_name'
                            value={last_name}
                            onChange={this.handleChange}
                        />
                    </Form.Field>
                </Form.Group>
                <Form.Group widths='equal'>
                    <Form.Field>
                        <label>כתובת</label>
                        <Form.Input
                            // type="password"
                            placeholder='כתובת'
                            name='adress'
                            value={adress}
                            onChange={this.handleChange}
                        />
                    </Form.Field>
                    <Form.Field>
                        <label>ת.ז</label>
                        <Form.Input
                            // type="password"
                            placeholder='ת.ז (שם משתמש)'
                            name='id_number'
                            value={id_number}
                            onChange={this.handleChange}
                        />
                        <Label size="tiny" pointing>שם המשתמש</Label>
                    </Form.Field>
                </Form.Group>
                <Form.Group>
                <FormField>
                        <Button onClick={this.onSubmit} primary>הירשם</Button>
                    </FormField>
                </Form.Group>
            </Form>
        )
    }
}

export default function SignupForm () {
    return (
        <Container className="signup-box">
            <div className="right-align">
                <FormSignup></FormSignup>   
            </div>
        </Container>
        
    )
}
